mapper.select:
select qna_num, qna_title, nickname, content, to_char(writedate, 'yyyy"년 "mm"월 "dd"일"') writedate, to_char(rewritedate, 'yyyy"년 "mm"월 "dd"일"') rewritedate, qna_option
from qnaboard
where qna_num = #{qna_num}

mapper.update:
update qnaboard
set readcount = readcount + 1
where qna_num = #{qna_num}

mapper.select:
select qna_num, qnac_title, nickname, content, to_char(writedate, 'yyyy"년 "mm"월 "dd"일"') writedate, to_char(rewritedate, 'yyyy"년 "mm"월 "dd"일"') rewritedate, readcount
from qnaboardcomment
where qna_num = #{qna_num}

mapper.update:
update qnaboardcomment
set readcount = readcount + 1
where qna_num = #{qna_num}

update qnaboard
set qna_option = 0
where qna_num = #{qna_num}
